(47, 'arrondi vers le haut', 'ceil()', '<h3>Symbole :</h3>\r\n\r\n<p>ceil()</p>\r\n', '<p>#include &lt;iostream&gt;<br />\r\n<span style="color:#B22222">#include</span>&nbsp;&lt;<span style="color:#0000FF">cmath</span>&gt;&nbsp;<span style="color:#008000">&nbsp;//Ne pas oublier cette ligne</span><br />\r\nusing namespace std;</p>\r\n\r\n<p>int main()<br />\r\n{<br />\r\n&nbsp; &nbsp;&nbsp;<span style="color:#0000FF">double&nbsp;</span><span style="color:#FF0000">nombre</span>(<span style="color:#FFD700">16</span>);<br />\r\n&nbsp; &nbsp;&nbsp;<span style="color:#0000FF">double&nbsp;</span><span style="color:#FF0000">resultat</span>;</p>\r\n\r\n<p>&nbsp; &nbsp;&nbsp;<span style="color:#FF0000">resultat&nbsp;</span>=&nbsp;<span style="color:#FFFFFF"><span style="background-color:#0000FF">ceil(</span></span>&nbsp;<span style="color:#FF0000">nombre&nbsp;</span><span style="background-color:#0000FF">)</span>&nbsp;&nbsp;;</p>\r\n\r\n<p>&nbsp; &nbsp;&nbsp;<span style="color:#0000FF">cout&nbsp;</span>&lt;&lt;&nbsp;<span style="color:#A9A9A9">&quot;Arrondi vers le haut</span><span style="color:#A9A9A9">&quot;</span>&lt;&lt;&nbsp;<span style="color:#FF0000">nombre</span>&lt;&lt;<span style="color:#A9A9A9">&nbsp;&quot;&nbsp;est&nbsp;&quot;&nbsp;</span>&lt;&lt;&nbsp;<span style="color:#FF0000">resultat&nbsp;</span>&lt;&lt; endl;</p>\r\n\r\n<p>&nbsp; &nbsp; return 0;<br />\r\n}</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', '', '0', '0', '0', 21),
(49, 'la fonction puissance', 'pow()', '<h3>Symbole :</h3>\r\n\r\n<p>pow()</p>\r\n\r\n<p>Je d&eacute;clare une variable pour mettre le&nbsp;4,</p>\r\n\r\n<p>une autre pour stocker le&nbsp;5&nbsp;</p>\r\n\r\n<p>et finalement une derni&egrave;re pour le r&eacute;sultat.</p>\r\n\r\n<p>J&#39;utilise la fonction<u><em><strong><span style="color:#FFFFFF"><span style="background-color:#0000FF">&nbsp;</span><code><span style="background-color:#0000FF">pow()</span></code></span></strong></em></u>&nbsp;pour effectuer le calcul</p>\r\n\r\n<p>et j&#39;utilise le symbole&nbsp;<strong>=</strong>&nbsp;pour initialiser la variable&nbsp;<code>resultat</code>&nbsp;avec la valeur calcul&eacute;e par la fonction.</p>\r\n', '<p>#include &lt;iostream&gt;<br />\r\n<span style="color:#B22222">#include</span>&nbsp;&lt;<span style="color:#0000FF">cmath</span>&gt;&nbsp;<span style="color:#008000">&nbsp;//Ne pas oublier cette ligne</span><br />\r\nusing namespace std;</p>\r\n\r\n<p>int main()<br />\r\n{<br />\r\n&nbsp; &nbsp;&nbsp;<span style="color:#0000FF">double </span><span style="color:#FF0000">a</span>(<span style="color:#FFD700">4</span>);<br />\r\n<span style="color:#0000FF">double </span><span style="color:#FF0000">b</span>(<span style="color:#FFD700">5</span>);<br />\r\n<span style="color:#0000FF">double </span><span style="color:#FF0000">resultat </span>= <span style="color:#FFFFFF"><span style="background-color:#0000FF">pow(</span></span><span style="color:#FF0000">a &nbsp;</span>,<span style="color:#FF0000"> &nbsp;b&nbsp;</span><span style="color:#FFFFFF"><span style="background-color:#0000FF">)</span></span>;</p>\r\n\r\n<p>&nbsp; &nbsp; return 0;<br />\r\n}</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', '', '0', '0', '0', 21),
(50, 'les symboles', '&agrave; conna&icirc;tre', '<blockquote>\r\n<h3><strong><em><u>Voici les diff&eacute;rentes symboles des condition a conna&icirc;tre&nbsp;</u></em></strong></h2>\r\n</blockquote>\r\n\r\n<h2>&nbsp;</h3>\r\n\r\n<table id="r-1905333">\r\n	<thead>\r\n		<tr>\r\n			<th>\r\n			<p>Symbole</p>\r\n			</th>\r\n			<th>\r\n			<p>Signification</p>\r\n			</th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p>==</p>\r\n			</td>\r\n			<td>\r\n			<p>Est &eacute;gal &agrave;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>&gt;</p>\r\n			</td>\r\n			<td>\r\n			<p>Est sup&eacute;rieur &agrave;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>&lt;</p>\r\n			</td>\r\n			<td>\r\n			<p>Est inf&eacute;rieur &agrave;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>&gt;=</p>\r\n			</td>\r\n			<td>\r\n			<p>Est sup&eacute;rieur ou &eacute;gal &agrave;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>&lt;=</p>\r\n			</td>\r\n			<td>\r\n			<p>Est inf&eacute;rieur ou &eacute;gal &agrave;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>!=</p>\r\n			</td>\r\n			<td>\r\n			<p>Est diff&eacute;rent de</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', '<p><u><em><strong>a conna&icirc;tre&nbsp;&nbsp;par coeur</strong></em></u></p>\r\n', '', '0', '0', '0', 25),
(51, 'la condition', 'if', '<p><span style="color:#FFFFFF"><span style="background-color:#0000FF">if</span></span>&nbsp;<span style="color:#FF0000"><span style="background-color:#0000FF">(</span></span>&nbsp;Entre parenth&egrave;se on donne les condition de validation &nbsp;<span style="color:#FF0000"><span style="background-color:#0000FF">)</span></span></p>\n\n<p><span style="color:#FF0000"><span style="background-color:#0000FF">{</span></span></p>\n\n<p>entre accolades ce qui faux faire si la condition est vrai</p>\n\n<p><span style="color:#FF0000"><span style="background-color:#0000FF">}</span></span></p>\n', '<p>#include &lt;iostream&gt;</p>\r\n\r\n<p>using namespace std;</p>\r\n\r\n<p>int main()<br />\r\n{<br />\r\n&nbsp; &nbsp; <span style="color:#0000FF">int </span><span style="color:#FF0000">nbEnfants</span>(<span style="color:#FF8C00">2</span>);</p>\r\n\r\n<p>&nbsp; &nbsp; <span style="color:#FFFFFF"><span style="background-color:#0000FF">if</span></span> (<span style="color:#FF0000">nbEnfants </span>&gt; <span style="color:#FF8C00">0</span>)<br />\r\n&nbsp; &nbsp; {<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; <span style="color:#0000FF">cout </span>&lt;&lt;<span style="color:#A9A9A9"> &quot;Vous avez des enfants, bravo !&quot;</span> &lt;&lt; endl;<br />\r\n&nbsp; &nbsp; }</p>\r\n\r\n<p>&nbsp; &nbsp; <span style="color:#0000FF">cout </span>&lt;&lt; <span style="color:#A9A9A9">&quot;Fin du programme&quot; </span>&lt;&lt; endl;<br />\r\n&nbsp; &nbsp; return 0;<br />\r\n}</p>\r\n', '', '0', '0', '0', 25),
(52, 'if -  sinon', 'else ', '<h3>Si dans le <span style="color:#0000FF">if </span>la <span style="color:#FF0000"><strong>condition est fausse</strong></span> :</h3>\r\n\r\n<ol>\r\n	<li>\r\n	<p>Il ne <u>lis pas</u> ce qu&#39;il y a dans <strong>les accolades</strong> .</p>\r\n	</li>\r\n	<li>\r\n	<p>Passe a la suite .</p>\r\n	</li>\r\n</ol>\r\n\r\n<h3>Si il y a un else :</h3>\r\n\r\n<p>il lira le <span style="color:#FFFFFF"><span style="background-color:#0000FF">else </span></span>qui signifie sinon&nbsp;</p>\r\n', '<p>#include &lt;iostream&gt;</p>\r\n\r\n<p>using namespace std;</p>\r\n\r\n<p>int main()<br />\r\n{<br />\r\n&nbsp; &nbsp; <span style="color:#0000FF">int</span> <span style="color:#FF0000">nbEnfants</span>(0);<span style="color:#008000">//il y n&#39;a <u><strong>aucun </strong></u>enfants</span><br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; <span style="color:#0000FF">cout </span>&lt;&lt; <span style="color:#A9A9A9">&quot;combien d&#39;enfants avez-vous ?&quot; </span>;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; <span style="color:#0000FF">cin </span>&gt;&gt; <span style="color:#FF0000">nbEnfants </span>;</p>\r\n\r\n<p>&nbsp; &nbsp; <span style="background-color:#ADD8E6">if (nbEnfants &gt; 0)</span> &nbsp;<span style="color:#008000">// Forcement la condition est fausse</span><br />\r\n&nbsp; &nbsp; <span style="background-color:#ADD8E6">{</span><br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; <span style="color:#0000FF">cout </span>&lt;&lt;<span style="color:#A9A9A9"> &quot;Vous avez des enfants, bravo !&quot;</span> &lt;&lt; endl;<br />\r\n&nbsp; &nbsp; <span style="background-color:#ADD8E6">}</span><br />\r\n&nbsp; &nbsp; <span style="color:#FFFFFF"><span style="background-color:#0000FF">else</span></span><br />\r\n&nbsp; &nbsp; <span style="background-color:#ADD8E6">{</span><br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; cout &lt;&lt; &quot;pas d&#39;enfant ? domage !&quot; ; <span style="color:#008000">//Donc c&#39;est le </span><span style="color:#FFFFFF"><span style="background-color:#0000FF">else</span></span>&nbsp;<span style="color:#008000">&nbsp;que l&#39;on &eacute;coute&nbsp;</span><br />\r\n&nbsp; &nbsp; <span style="background-color:#ADD8E6">}</span></p>\r\n\r\n<p>&nbsp; &nbsp; <span style="color:#0000FF">cout </span>&lt;&lt;<span style="color:#A9A9A9"> &quot;Fin du programme&quot; </span>&lt;&lt; endl;<br />\r\n&nbsp; &nbsp; return 0;<br />\r\n}<br />\r\n&nbsp;</p>\r\n', 'openclassrooms.com', '0', '0', '0', 25),
(53, 'if - else if', 'plusieurs conditions', '<h3>Si il y a <span style="color:#FF0000">plusieurs conditions </span>inutile d&#39;encha&icirc;ner les if</h3>\r\n\r\n<p>&nbsp;</p>\r\n', '<p>#include &lt;iostream&gt;</p>\r\n\r\n<p>using namespace std;</p>\r\n\r\n<p>int main()<br />\r\n{<br />\r\n&nbsp; &nbsp; <span style="color:#FF0000">int nbEnfants</span>(5);</p>\r\n\r\n<p>&nbsp; &nbsp; <span style="background-color:#ADD8E6">if (nbEnfants == 0)</span><br />\r\n&nbsp; &nbsp; {<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; <span style="color:#0000FF">cout </span>&lt;&lt;<span style="color:#808080"> &quot;Eh bien alors, vous n&#39;avez pas d&#39;enfant ?&quot;</span><span style="color:#A9A9A9"> </span>&lt;&lt; endl;<br />\r\n&nbsp; &nbsp; }<br />\r\n<span style="background-color:#ADD8E6">&nbsp; &nbsp; </span><span style="color:#FFFFFF"><span style="background-color:#0000FF">else if</span></span><span style="background-color:#ADD8E6"> (nbEnfants == 1)</span><br />\r\n&nbsp; &nbsp; {<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; <span style="color:#0000FF">cout </span>&lt;&lt; <span style="color:#808080">&quot;Alors, c&#39;est pour quand le deuxieme ?&quot;</span> &lt;&lt; endl;<br />\r\n&nbsp; &nbsp; }<br />\r\n<span style="background-color:#ADD8E6">&nbsp; &nbsp; </span><span style="color:#FFFFFF"><span style="background-color:#0000FF">else if</span></span><span style="background-color:#ADD8E6"> (nbEnfants == 2)</span><br />\r\n&nbsp; &nbsp; {<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; <span style="color:#0000FF">cout </span>&lt;&lt;<span style="color:#808080"> &quot;Quels beaux enfants vous avez la !&quot;</span> &lt;&lt; endl;<br />\r\n&nbsp; &nbsp; }<br />\r\n<span style="background-color:#ADD8E6">&nbsp; &nbsp; else</span><br />\r\n&nbsp; &nbsp; {<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; <span style="color:#0000FF">cout </span>&lt;&lt;<span style="color:#808080"> &quot;Bon, il faut arreter de faire des gosses maintenant !&quot;</span> &lt;&lt; endl;<br />\r\n&nbsp; &nbsp; }</p>\r\n\r\n<p>&nbsp; &nbsp; <span style="color:#0000FF">cout </span>&lt;&lt; <span style="color:#808080">&quot;Fin du programme&quot;</span> &lt;&lt; endl;<br />\r\n&nbsp; &nbsp; return 0;<br />\r\n}</p>\r\n', 'openclassrooms.com', '0', '0', '0', 25),
(54, 'switch', 'faire plusieurs teste', '<p>1.<u><strong>la variable</strong></u></p>\r\n\r\n<ul>\r\n	<li>\r\n	<pre>\r\nTout les teste serons fais sur une variable</pre>\r\n	</li>\r\n	<li>\r\n	<pre>\r\nChaque teste d&eacute;termineras si la variable est &eacute;gale &agrave; [...]</pre>\r\n	</li>\r\n</ul>\r\n\r\n<p>2.<strong><u>case&nbsp;</u></strong></p>\r\n\r\n<ul>\r\n	<li>\r\n	<pre>\r\nLe &laquo;&nbsp;case&nbsp;&raquo; c&#39;est les cas \r\n<u><strong>EXEMPLES</strong></u>&nbsp;<strong><u>:</u></strong>\r\n<span style="background-color:#DDA0DD">case(8)</span> la variable fais 8 \r\n</pre>\r\n	</li>\r\n	<li>\r\n	<pre>\r\n<span style="background-color:#ADD8E6">case(&#39;Philippe&#39;)</span> la variable fais  Philippe\r\n</pre>\r\n	</li>\r\n</ul>\r\n\r\n<p>3.<u><strong>break;</strong></u></p>\r\n\r\n<ul>\r\n	<li>Les&nbsp;<span style="color:#0000FF"><code>break</code>&nbsp;</span>sont obligatoires si on veut que l&#39;ordinateur arr&ecirc;te les tests une fois que l&#39;un d&#39;eux a r&eacute;ussi.&nbsp;</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', '<p>#include &lt;iostream&gt;</p>\r\n\r\n<p>using namespace std;</p>\r\n\r\n<p>int main()<br />\r\n{<br />\r\n&nbsp; &nbsp; <span style="color:#0000FF">int </span><span style="color:#FF0000">nbEnfants</span>(<span style="color:#DAA520">4</span>);</p>\r\n\r\n<p><span style="background-color:#ADD8E6">&nbsp; &nbsp; switch (nbEnfants)</span> <span style="color:#008000">//S&eacute;lection de la variable</span></p>\r\n\r\n<p>&nbsp; &nbsp; {<br />\r\n<span style="background-color:#ADD8E6">&nbsp; &nbsp; &nbsp; &nbsp; case 0:</span><br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span style="color:#0000FF">cout </span>&lt;&lt; <span style="color:#808080">&quot;Eh bien alors, vous n&#39;avez pas d&#39;enfant ?&quot; </span>&lt;&lt; endl;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span style="color:#0000FF">break</span>;</p>\r\n\r\n<p><span style="background-color:#ADD8E6">&nbsp; &nbsp; &nbsp; &nbsp; case 1:</span><br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span style="color:#0000FF">cout </span>&lt;&lt; <span style="color:#808080">&quot;Alors, c&#39;est pour quand le deuxieme ?&quot;</span> &lt;&lt; endl;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span style="color:#0000FF">break</span>;</p>\r\n\r\n<p><span style="background-color:#ADD8E6">&nbsp; &nbsp; &nbsp; &nbsp; case 2:</span><br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span style="color:#0000FF">cout </span>&lt;&lt; <span style="color:#808080">&quot;Quels beaux enfants vous avez la !&quot;</span> &lt;&lt; endl;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span style="color:#0000FF">break</span>;</p>\r\n\r\n<p><span style="background-color:#ADD8E6">&nbsp; &nbsp; &nbsp; &nbsp; default:</span><br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span style="color:#0000FF">cout </span>&lt;&lt; <span style="color:#808080">&quot;Bon, il faut arreter de faire des gosses maintenant !&quot;</span> &lt;&lt; endl;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span style="color:#0000FF">break</span>;<br />\r\n&nbsp; &nbsp; }</p>\r\n\r\n<p>&nbsp; &nbsp; return 0;<br />\r\n}</p>\r\n', 'openclassrooms.com', '0', '0', '0', 25),
(55, 'les bool&eacute;ens', 'oui ou non', '<p>Ce type est souvent utilis&eacute; avec les conditions.</p>\r\n\r\n<p>Quand on y pense, c&#39;est logique : une condition est soit vraie, soit fausse.</p>\r\n\r\n<p>Une variable bool&eacute;enne aussi.</p>\r\n\r\n<p>Il y a deux fa&ccedil;on de l&rsquo;&eacute;crire&nbsp;:<br />\r\n<span style="background-color:#ADD8E6">if(variable == condition )</span><br />\r\nou<br />\r\n<span style="background-color:#ADD8E6">if(variable)</span><span style="color:#008000">&nbsp; // Si il n&#39;y a que la variable l&#39;ordinateur comprend&nbsp;que c&#39;est une variable bool&eacute;en</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', '<h3>ancienne fa&ccedil;on</h3>\r\n\r\n<p>bool adulte(true);</p>\r\n\r\n<p><span style="background-color:#ADD8E6">if (adulte == true)</span><br />\r\n{<br />\r\n&nbsp; &nbsp; <span style="color:#0000FF">cout</span> &lt;&lt; <span style="color:#808080">&quot;Vous etes un adulte !&quot;</span> &lt;&lt; endl;<br />\r\n}</p>\r\n\r\n<h3>nouvelle fa&ccedil;on</h3>\r\n\r\n<p>bool adulte(true);</p>\r\n\r\n<p><span style="background-color:#ADD8E6">if (adulte)</span><br />\r\n{<br />\r\n&nbsp; &nbsp; <span style="color:#0000FF">cout</span> &lt;&lt; <span style="color:#808080">&quot;Vous etes un adulte !&quot;</span> &lt;&lt; endl;<br />\r\n}</p>\r\n', 'openclassrooms.com', '0', '0', '0', 25),
(56, 'nouveaux symboles', 'et - ou - non', '<ul>\r\n	<li>&amp;&amp; =&gt; ET</li>\r\n	<li>|| =&gt; OU ( ALT + 6 sur le clavier)</li>\r\n	<li>or ( <span style="color:#FF0000">! &nbsp;<span style="color:rgb(51, 51, 51); line-height:1.6; white-space:pre-wrap">inuscules </span></span>) =&gt; OU&nbsp;</li>\r\n	<li>! =&gt; NON</li>\r\n</ul>\r\n', '<p>if (adulte <span style="color:#FFFFFF"><span style="background-color:#0000FF">&amp;&amp;</span></span> nbEnfants &gt;= 1)&nbsp;<span style="color:#008000">// ET</span></p>\r\n\r\n<p>if (adulte <span style="color:#FFFFFF"><span style="background-color:#0000FF">||</span></span> nbEnfants &gt;= 1) <span style="color:#008000">//OU</span></p>\r\n\r\n<p>if (&nbsp;<span style="color:#FFFFFF"><strong><span style="background-color:#0000CD">!</span></strong></span>adulte)<span style="color:#008000"> // NON&nbsp;</span></p>\r\n', 'openclassrooms.com', '0', '0', '0', 25),
(57, 'la boucles', 'while', '<h3>Tout ce qui est entre accolades sera r&eacute;p&eacute;t&eacute; tant que la condition est v&eacute;rifi&eacute;e.</h3>\r\n\r\n<ol>\r\n	<li>&nbsp;Une variable doit &ecirc;tre d&eacute;finie avant</li>\r\n	<li>\r\n	<p>On&nbsp;<span style="line-height:1.6; white-space:pre-wrap">peut la comprendre par &hellip; <span style="color:#FFFFFF"><span style="background-color:#0000CD">tant que </span></span></span></p>\r\n	</li>\r\n</ol>\r\n\r\n<p>&nbsp;</p>\r\n', '<p>int main()<br />\r\n{<br />\r\n&nbsp; &nbsp; <span style="color:#0000FF">int </span><span style="color:#FF0000">nbEnfants</span>(-1); <span style="color:#008000">// D&eacute;claration de la variable</span></p>\r\n\r\n<p><span style="line-height:1.6"><span style="background-color:#ADD8E6">&nbsp; &nbsp; while (nbEnfants &lt; 0)</span></span></p>\r\n\r\n<p>&nbsp; &nbsp; {<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; <span style="color:#0000FF">cout </span>&lt;&lt;<span style="color:#696969"> &quot;Combien d&#39;enfants avez-vous ?&quot;</span> &lt;&lt; endl;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; <span style="color:#0000FF">cin </span>&gt;&gt; <span style="color:#FF0000">nbEnfants</span>;<br />\r\n&nbsp; &nbsp; }</p>\r\n\r\n<p>&nbsp; &nbsp; <span style="color:#0000FF">cout </span>&lt;&lt;<span style="color:#696969"> &quot;Merci d&#39;avoir indique un nombre d&#39;enfants correct. Vous en avez &quot;</span> &lt;&lt; <span style="color:#FF0000">nbEnfants </span>&lt;&lt; endl;</p>\r\n\r\n<p>&nbsp; &nbsp; return 0;<br />\r\n}</p>\r\n', 'openclassrooms.com', '0', '0', '0', 26),
(58, 'la boucle', 'do ... while', '<p>Cette boucle est tr&egrave;s similaire &agrave; <span style="color:#0000FF">while </span>... si ce n&#39;est que la condition n&#39;est test&eacute;e qu&#39;&agrave; la fin.</p>\r\n\r\n<p>Cela signifie que le contenu de <span style="color:#FF8C00"><u><em><strong>la boucle sera toujours lu&nbsp;au moins une fois.</strong></em></u></span></p>\r\n\r\n<ul>\r\n	<li>Notez bien la pr&eacute;sence du <span style="color:#FF8C00">point-virgule </span>tout &agrave; la fin !</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n', '<p>int main()<br />\r\n{<br />\r\n&nbsp; &nbsp; <span style="color:#0000FF">int </span><span style="color:#FF0000">nbEnfants</span>(<span style="color:#FF8C00">0</span>);</p>\r\n\r\n<p><span style="background-color:#000080">&nbsp; &nbsp; do</span><br />\r\n&nbsp; &nbsp; {<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; <span style="color:#000080">cout </span>&lt;&lt;<span style="color:#696969"> &quot;Combien d&#39;enfants avez-vous ?&quot;</span> &lt;&lt; endl;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; <span style="color:#000080">cin </span>&gt;&gt; <span style="color:#FF0000">nbEnfants</span>;<br />\r\n&nbsp; &nbsp; }<span style="background-color:#000080"> while (nbEnfants &lt; 0)</span> <span style="background-color:#000080">;</span></p>\r\n\r\n<p>&nbsp; &nbsp; <span style="color:#000080">cout </span>&lt;&lt; <span style="color:#696969">&quot;Merci d&#39;avoir indique un nombre d&#39;enfants correct. Vous en avez &quot;</span> &lt;&lt; <span style="color:#FF0000">nbEnfants </span>&lt;&lt; endl;</p>\r\n\r\n<p>&nbsp; &nbsp; return 0;<br />\r\n}</p>\r\n', '', '0', '0', '0', 26),
(59, 'la boucle', 'for', '<p>Ce type de boucle, que l&#39;on retrouve fr&eacute;quemment, permet de condenser :</p>\r\n\r\n<ul>\r\n	<li>\r\n	<p>une initialisation ;</p>\r\n	</li>\r\n	<li>\r\n	<p>une condition ;</p>\r\n	</li>\r\n	<li>\r\n	<p>une incr&eacute;mentation.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p><strong><u>Notez qu&#39;il est courant d&#39;initialiser la variable directement &agrave; l&#39;int&eacute;rieur du&nbsp;<code>for</code>,</u></strong></p>\r\n\r\n<p><u>La variable n&#39;existe alors que <em><strong><span style="color:#FF8C00">pendant la dur&eacute;e de la boucle</span>&nbsp;</strong></em><code>for</code>.</u></p>\r\n\r\n<p>C&#39;est la forme la plus courante de cette boucle.</p>\r\n\r\n<p>On ne d&eacute;clare la variable avant le&nbsp;<code>for</code>&nbsp;que si on en a besoin plus tard, ce qui est un cas assez rare.</p>\r\n', '<p>int main()<br />\r\n{<br />\r\n&nbsp; &nbsp; int compteur(0);</p>\r\n\r\n<p>&nbsp; &nbsp; for (compteur = 0 ; compteur &lt; 10 ; compteur++)<br />\r\n&nbsp; &nbsp; {<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; cout &lt;&lt; compteur &lt;&lt; endl;<br />\r\n&nbsp; &nbsp; }</p>\r\n\r\n<p>&nbsp; &nbsp; return 0;<br />\r\n}</p>\r\n', 'openclassrooms.com', '0', '0', '0', 26),
(60, 'pr&eacute;sentation', 'des fonctions', '<ul>\r\n	<li>Les donn&eacute;es entrantes s&#39;appellent des&nbsp;<strong>arguments</strong></li>\r\n	<li>&nbsp;et on utilise l&#39;expression&nbsp;<strong>valeur retourn&eacute;e</strong>&nbsp;pour les &eacute;l&eacute;ments qui sortent de la fonction</li>\r\n</ul>\r\n\r\n<hr />\r\n<ul>\r\n	<li>\r\n	<h3><u>Le premier &eacute;l&eacute;ment est le&nbsp;<strong>type de retour</strong>. Il permet d&#39;indiquer le type de variable renvoy&eacute;e par la fonction. </u></h3>\r\n	</li>\r\n</ul>\r\n\r\n<p>Si votre fonction doit renvoyer du texte, alors ce sera&nbsp;<code>string</code>&nbsp;; si votre fonction effectue un calcul, alors ce sera<code>int</code>&nbsp;ou&nbsp;<code>double</code>.</p>\r\n\r\n<ul>\r\n	<li>\r\n	<h3><u>Le deuxi&egrave;me &eacute;l&eacute;ment est le&nbsp;<strong>nom de la fonction</strong>.</u></h3>\r\n	</li>\r\n</ul>\r\n\r\n<p>Vous connaissez d&eacute;j&agrave;&nbsp;<code>main()</code>,&nbsp;<code>pow()</code>&nbsp;ou&nbsp;<code>sqrt()</code>. L&#39;important est de choisir un nom de fonction qui d&eacute;crit bien ce qu&#39;elle fait, comme pour les variables, en fait.</p>\r\n\r\n<ul>\r\n	<li>\r\n	<h3>Entre les parenth&egrave;ses, on trouve la&nbsp;<strong>liste des arguments</strong>&nbsp;de la fonction.</h3>\r\n	</li>\r\n</ul>\r\n\r\n<p>Ce sont les donn&eacute;es avec lesquelles la fonction va travailler. Il peut y avoir un argument (comme pour&nbsp;<code>sqrt()</code>), plusieurs arguments (comme pour<code>pow()</code>) ou aucun argument (comme pour&nbsp;<code>main()</code>)</p>\r\n\r\n<ul>\r\n	<li>\r\n	<h3><u>Finalement, il y a des&nbsp;<strong>accolades</strong>&nbsp;qui d&eacute;limitent le contenu de la fonction.</u></h3>\r\n	</li>\r\n</ul>\r\n\r\n<p>Toutes les op&eacute;rations qui seront effectu&eacute;es se trouvent entre les deux accolades.</p>\r\n\r\n<hr />\r\n<p><span style="color:#FFFFFF"><span style="background-color:#800080">Il est possible de cr&eacute;er&nbsp;plusieurs fonctions ayant le </span></span><span style="color:#FF8C00"><u><strong>m&ecirc;me nom.</strong></u></span><span style="color:#FFFFFF"><span style="background-color:#800080">&nbsp;Il faut alors que la </span></span><span style="color:#FF8C00"><u><strong>liste des arguments des deux fonctions soit diff&eacute;rente.</strong></u></span><span style="color:#FFFFFF"><span style="background-color:#800080"> C&#39;est ce qu&#39;on appelle la&nbsp;</span></span><span style="color:#FF8C00"><u><strong>surcharge&nbsp;d&#39;une fonction</strong></u></span><span style="color:#FFFFFF"><span style="background-color:#800080">. </span></span></p>\r\n\r\n<p><span style="color:#FFFFFF"><span style="background-color:#800080">Dans un m&ecirc;me programme, il peut par exemple y avoir la fonction&nbsp;int addition(int a, int b)&nbsp;et la fonctiondouble addition(double a, double b). Les deux fonctions ont le m&ecirc;me nom mais l&#39;une travaille avec des entiers et l&#39;autre avec des nombres r&eacute;els.</span></span></p>\r\n', '<h3>D&eacute;finir une fonction</h3>\r\n\r\n<p><span style="color:#0000FF">type </span><span style="color:#FF0000">nomFonction</span>(<span style="color:#FF8C00">arguments</span>)<br />\r\n{<br />\r\n<span style="color:#008000">&nbsp; &nbsp; //Instructions effectu&eacute;es par la fonction</span><br />\r\n}</p>\r\n', 'openclassrooms.com', '1455925965.img1.png', '0', '0', 27),
(61, 'cr&eacute;e ', 'une fonction', '<h3>Pour cr&eacute;&eacute; une fonction il faut d&eacute;clarer :</h3>\r\n\r\n<ol>\r\n	<li>\r\n	<p><u>le&nbsp;<strong>type de retour</strong></u></p>\r\n	</li>\r\n	<li>\r\n	<p><u>le&nbsp;<strong>nom de la fonction</strong></u></p>\r\n	</li>\r\n	<li>\r\n	<p><strong><u>Les arguments&nbsp;de la fonction</u></strong></p>\r\n	</li>\r\n	<li>\r\n	<p><strong><u>return &nbsp;=&gt; la valeur de sortie&nbsp;</u></strong></p>\r\n	</li>\r\n</ol>\r\n\r\n<hr />\r\n<p>&nbsp;</p>\r\n\r\n<pre>\r\n\r\n&nbsp;</pre>\r\n', '<p><span style="color:#000080">type </span><span style="color:#FF0000">nomFonction</span>(<span style="color:#FF8C00">arguments</span>)<br />\r\n{<br />\r\n<span style="color:#008000">&nbsp; &nbsp; //Instructions effectu&eacute;es par la fonction</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style="color:#008000">&nbsp; &nbsp; </span><span style="color:#0000FF">return </span><span style="color:#FF0000">valeur</span><span style="color:#008000">; //&nbsp;Si la fonction doit renvoyer un argument , c&#39;est return qui d&eacute;fini la variable</span></p>\r\n\r\n<p><br />\r\n}</p>\r\n', 'openclassrooms.com', '0', '0', '0', 27),
(62, 'les fonctions avec', 'plusieurs param&egrave;tre', '<pre>\r\nSi lors de la cr&eacute;ation de la fonction plusieurs param&egrave;tre sont demand&eacute;es </pre>\r\n\r\n<blockquote>\r\n<p><span style="background-color:#DDA0DD">Pour passer plusieurs param&egrave;tres &agrave; une fonction, il faut les s&eacute;parer par des virgules.</span></p>\r\n</blockquote>\r\n\r\n<pre>\r\nIl faut que <u><strong><span style="color:#FF8C00">chaque variable</span></strong></u> a un <em><strong><u><span style="color:#0000FF">type</span></u></strong></em> d&eacute;fini ! </pre>\r\n\r\n<h3>Bien s&ucirc;r on peux &eacute;crire des fonctions qui prennent des <span style="color:#FF8C00">arguments </span>de <span style="color:#0000FF">type </span>diff&eacute;rent.</h3>\r\n', '<p><span style="color:#0000FF">int </span>addition(<span style="color:#0000FF">int </span><span style="color:#FF0000">a</span>, <span style="color:#0000FF">int </span><span style="color:#FF0000">b</span>)<br />\r\n{<br />\r\n&nbsp; &nbsp; return <span style="color:#FF0000">a</span>+<span style="color:#FF0000">b&nbsp;</span>;<br />\r\n}</p>\r\n\r\n<p><span style="color:#0000FF">double </span>multiplication(<span style="color:#0000FF">double </span><span style="color:#FF0000">a</span>, <span style="color:#0000FF">double </span><span style="color:#FF0000">b</span>, <span style="color:#0000FF">double </span><span style="color:#FF0000">c</span>) <span style="color:#006400">// Chaque&nbsp;Variables ce voit attribuer un type , rien interdis que les types soient diff&egrave;rent </span><br />\r\n{<br />\r\n&nbsp; &nbsp; return <span style="color:#FF0000">a</span>*<span style="color:#FF0000">b</span>*<span style="color:#FF0000">c</span> ;<br />\r\n}</p>\r\n', 'openclassrooms.com', '0', '0', '0', 27),
(63, 'les fonctions sans', 'arguments', '<p>Une fonction qui ne poss&egrave;de pas d&#39;argument est autoriser ,</p>\r\n\r\n<p>Bien que plus rare</p>\r\n', '<p><span style="color:#0000FF">string </span>demanderNom()<br />\r\n{ &nbsp; &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp;<span style="color:#0000FF">cout </span>&lt;&lt;<span style="color:#696969"> &quot;Entrez votre nom : &nbsp;&quot; &nbsp;</span>;<br />\r\n&nbsp; &nbsp; &nbsp;<span style="color:#DAA520">string </span><span style="color:#FF0000">nom</span>;<br />\r\n&nbsp; &nbsp; &nbsp;<span style="color:#0000FF">cin </span>&gt;&gt; <span style="color:#FF0000">nom</span>;<br />\r\n&nbsp; &nbsp; &nbsp;return <span style="color:#FF0000">nom</span>;<br />\r\n}</p>\r\n', 'openclassrooms.com', '0', '0', '0', 27),
(64, 'les fonctions', 'qui ne renvoient rien', '<h3><strong><u>Il est aussi possible d&#39;&eacute;crire des fonctions qui ne renvoient rien.</u></strong></h3>\r\n\r\n<p>Rien ne ressort de la fonction mais, quand on la d&eacute;clare, il faut quand m&ecirc;me indiquer un type.</p>\r\n\r\n<h3><u><em><strong>void</strong></em></u></h3>\r\n\r\n<p>On utilise le type <span style="color:#FFFFFF"><code><span style="background-color:#000080">void</span></code></span> , ce qui signifie &laquo; <span style="color:#000080">n&eacute;ant </span>&raquo; en anglais.</p>\r\n\r\n<p>Cela veut tout dire : il n&#39;y a vraiment <span style="color:#FF8C00"><strong><u>rien qui soit renvoy&eacute; par la fonction.</u></strong></span></p>\r\n', '<p><span style="color:#000080">void</span> direBonjour()<br />\r\n{<br />\r\n&nbsp; &nbsp; <span style="color:#0000FF">cout </span>&lt;&lt;<span style="color:#696969"> &quot;Bonjour !&quot;</span> &lt;&lt; endl;<br />\r\n<span style="color:#006400">&nbsp; &nbsp; //Comme rien ne ressort, il n&#39;y a pas de return !</span><br />\r\n}</p>\r\n\r\n<p>int main()<br />\r\n{<br />\r\n&nbsp; &nbsp; direBonjour();<br />\r\n<span style="color:#006400">&nbsp; &nbsp; //Comme la fonction ne renvoie rien<br />\r\n&nbsp; &nbsp; //On l&#39;appelle sans mettre la valeur de retour dans une variable</span><br />\r\n&nbsp; &nbsp;&nbsp;<br />\r\n&nbsp; &nbsp; return 0;<br />\r\n}</p>\r\n', 'openclassrooms.com', '0', '0', '0', 27),
(65, 'passage par valeur', 'gestion de la m&eacute;moire', '<p>Les fonctions que l&#39;on dit &laquo; <strong><u>Passage par valeur </u></strong>&raquo; , sont celle que nous effectuons <em><u>par d&eacute;faut</u></em> .</p>\r\n\r\n<p>Leur propri&eacute;t&eacute; est qu&#39;elle ne <span style="color:#FF0000">changer pas les variable d&eacute;fini</span> comme <u><em><span style="color:#FF8C00">argument</span></em></u> .</p>\r\n\r\n<p>Pour cela l&#39;ordinateur <strong><u><span style="color:#FF8C00">cr&eacute;e une nouvelle m&eacute;moire</span></u></strong> contenant une <strong><u><span style="color:#FF8C00">copie de l&#39;argument</span></u></strong> envoyer</p>\r\n', '<p>pas cette fois-ci :)</p>\r\n', 'openclassrooms.com', '1455936581.img1.png', '0', '0', 27),
(66, 'passage par r&eacute;f&eacute;rence', 'gestion de la m&eacute;moire', '<p>les r&eacute;f&eacute;rences &hellip; gr&acirc;ce a elles ,nous pouvons demander a une fonction de <u><span style="color:#FF8C00">modifier une variable </span></u>se trouvant <span style="color:#FF8C00"><u>hors de la fonction</u></span><span style="color:#DAA520"> </span>!</p>\r\n\r\n<p>C&#39;est assez dangereux car la variable est <strong><span style="color:#FF0000">modifier definitivement</span></strong>&nbsp;!</p>\r\n\r\n<p>Lorsque l&#39;on appelle la fonction, il <strong><u>n&#39;y a plus de copie</u></strong>.</p>\r\n\r\n<p>Le programme donne simplement <u><strong>un alias &agrave; la variable</strong></u>.</p>\r\n', '<p><span style="color:#000080">int </span>ajouteDeux(<span style="color:#000080">int</span><span style="color:#800080">&amp;</span> <span style="color:#FF0000">a</span>)<span style="color:#006400"> //Notez le petit &amp; !!!</span><br />\r\n{<br />\r\n&nbsp; &nbsp; <span style="color:#FF0000">a&nbsp;</span>+= <span style="color:#DAA520">2</span>;<br />\r\n&nbsp; &nbsp; return <span style="color:#FF0000">a</span>;<br />\r\n}</p>\r\n', 'openclassrooms.com', '1455939090.img1.png', '0', '0', 27),
(67, 'les fonctions par', 'passage par r&eacute;f&eacute;rence constante', '<p>Malgr&eacute; le mot barbare c&#39;est assez simple .</p>\r\n\r\n<p>C&#39;est une fonction par <strong><u><span style="background-color:#DDA0DD">&lt;&lt; passage par r&eacute;f&eacute;rence &gt;&gt;</span></u></strong>.</p>\r\n\r\n<p>Ou on l&#39;as <span style="color:#FF8C00"><strong><u>v</u></strong><u><strong>errouill&eacute;e grasse a la constance</strong></u></span></p>\r\n', '<p><span style="color:#000080">void</span> nomFonction(<span style="color:#000080">string</span> <u><strong><span style="color:#4B0082">const</span></strong></u><span style="color:#FFFFFF"><span style="background-color:#800080">&amp;</span></span> texte); &nbsp;<span style="color:#006400">//Pas de copie et pas de modification possible</span><br />\r\n{</p>\r\n\r\n<p><br />\r\n}</p>\r\n', 'openclassrooms.com', '0', '0', '0', 27),
(68, 'cr&eacute;e un fichier source', 'explication', '<p>&nbsp;</p>\r\n\r\n<ol>\r\n	<li>C&#39;est le plus simple des deux. Passez par les menus&nbsp;<code>File</code>&nbsp;&gt;&nbsp;<code>New</code>&nbsp;&gt;&nbsp;<code>File</code>. Choisissez ensuite&nbsp;<code>C/C++ source</code>(figure suivante).</li>\r\n	<li>\r\n	<p>Cliquez ensuite sur&nbsp;<code>Go</code>. Comme lors de la cr&eacute;ation du projet, le programme vous demande alors si vous faites du C ou du C++. Choisissez&nbsp;<code>C++</code>&nbsp;bien s&ucirc;r !</p>\r\n	</li>\r\n	<li>\r\n	<p>Finalement, on vous demande le nom du fichier &agrave; cr&eacute;er. Comme pour tout, il vaut mieux choisir un nom intelligent pour ses fichiers. On peut ainsi mieux s&#39;y retrouver. Pour la fonction&nbsp;<code>ajouteDeux()</code>, je choisis le nom&nbsp;<code>math.cpp</code>et je place le fichier dans le m&ecirc;me dossier que mon fichier&nbsp;<code>main.cpp</code>.</p>\r\n	</li>\r\n	<li>\r\n	<p>Cochez ensuite toutes les options (figure suivante).</p>\r\n	</li>\r\n	<li>\r\n	<p>Cliquez sur&nbsp;<code>Finish</code>. Votre fichier source est maintenant cr&eacute;&eacute;. Passons au fichier d&#39;en-t&ecirc;te.</p>\r\n	</li>\r\n</ol>\r\n\r\n<p>&nbsp;</p>\r\n', '<p>rien pour le moment</p>\r\n', 'openclassrooms.com', '1455941736.img1.png', '1455941737.img2.png', '0', 12),
(69, 'cr&eacute;e fichier d''en-t&ecirc;te', 'explication', '<ol>\r\n	<li>\r\n	<p>Le d&eacute;but est quasiment identique. Passez par les menus&nbsp;<code>File</code>&nbsp;&gt;&nbsp;<code>New</code>&nbsp;&gt;&nbsp;<code>File</code>. S&eacute;lectionnez ensuite&nbsp;<code>C/C++ header</code>&nbsp;(figure suivante).</p>\r\n	</li>\r\n	<li>\r\n	<p>Dans la fen&ecirc;tre suivante, indiquez le nom du fichier &agrave; cr&eacute;er. Il est conseill&eacute; de lui donner le m&ecirc;me nom qu&#39;au fichier source mais avec une extension&nbsp;<code>.h</code>&nbsp;au lieu de&nbsp;<code>.cpp</code>. Dans notre cas, ce sera donc&nbsp;<code>math.h</code>. Placez le fichier dans le m&ecirc;me dossier que les deux autres.</p>\r\n	</li>\r\n	<li>\r\n	<p>Cliquez sur&nbsp;<code>Finish</code>. Et voil&agrave; !</p>\r\n	</li>\r\n	<li>\r\n	<p>Une fois que les deux fichiers sont cr&eacute;&eacute;s, vous devriez les voir appara&icirc;tre dans la colonne de gauche de Code::Blocks (figure suivante).</p>\r\n	</li>\r\n</ol>\r\n\r\n<p>&nbsp;</p>\r\n', '<p>rien pour le moment</p>\r\n', 'openclassrooms.com', '1455942036.img1.png', '1455942037.img2.png', '1455942037.img3.png', 12),
(70, 'le fichier source', '.cpp', '<p>1. Le compilateur a besoin de savoir que les fichiers&nbsp;<code>.cpp</code>&nbsp;et&nbsp;<code>.h</code>&nbsp;ont un lien entre eux.</p>\r\n\r\n<p><u><em><strong><span style="color:#FF0000">ATTENTION</span></strong></em></u> &nbsp;&nbsp;le nom du fichier est entre guillemet</p>\r\n\r\n<p>&nbsp;</p>\r\n', '<p><span style="color:#4B0082">#include</span> &quot;<span style="background-color:#B22222">math.h</span>&quot; <span style="color:#008000">// &nbsp;OBLIGATOIRE sinon le compilateur ne fais pas le lien entre les deux fichier</span> &nbsp;&nbsp; &nbsp;&nbsp;</p>\r\n\r\n<p><span style="color:#0000CD"><span style="background-color:#ADD8E6">int </span></span><span style="background-color:#ADD8E6">nomFonction( </span><span style="color:#0000CD"><strong><span style="background-color:#ADD8E6">bool </span></strong></span><span style="color:#FF0000"><span style="background-color:#ADD8E6">ouiOuNon&nbsp;</span></span><span style="background-color:#ADD8E6">)</span><br />\r\n{<br />\r\n&nbsp; &nbsp; <span style="color:#000080">int </span><span style="color:#FF0000">valeur</span>(nombreRecu + <span style="color:#DAA520">2</span>);</p>\r\n\r\n<p>&nbsp; &nbsp; <span style="color:#000080">return </span><span style="color:#FF0000">valeur</span>;<br />\r\n}</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'openclassrooms.com', '0', '0', '0', 28),
(71, 'le fichier d''en-t&ecirc;te', '.h', '<pre>\r\n&nbsp;</pre>\r\n\r\n<ol>\r\n	<li>\r\n	<p>Si vous regardez le fichier , Il contient trois lignes :</p>\r\n\r\n	<ul style="margin-left:40px">\r\n		<li>Ces lignes sont l&agrave; pour emp&ecirc;cher le compilateur d&#39;inclure plusieurs fois ce fichier.</li>\r\n		<li>Le compilateur n&#39;est parfois pas tr&egrave;s malin et risque de tourner en rond.</li>\r\n		<li>Cette astuce &eacute;vite donc de se retrouver dans cette situation.</li>\r\n		<li>Il ne faut donc pas toucher ces lignes et surtout, &eacute;crire tout le code&nbsp;<em>entre la deuxi&egrave;me et la troisi&egrave;me</em></li>\r\n	</ul>\r\n	</li>\r\n	<li>?????????Le texte en majuscules sera diff&eacute;rent pour chaque fichier.</li>\r\n	<li>Dans ce fichier, il faut mettre ce qu&#39;on appelle le&nbsp;<span style="color:#FFFFFF"><u><strong><span style="background-color:#000080">prototype</span></strong></u></span>&nbsp;de la fonction.\r\n	<ul>\r\n		<li>C&#39;est la <u><span style="color:#DAA520"><strong>premi&egrave;re ligne de la fonction</strong></span></u>, celle qui vient avant les accolades.</li>\r\n		<li>On copie le texte de cette ligne et on ajoute un <u><strong><span style="color:#B22222">point-virgule &agrave; la fin</span></strong></u>.</li>\r\n	</ul>\r\n	</li>\r\n	<li>A la fin on place le&nbsp;point-virgule</li>\r\n</ol>\r\n\r\n<h3><span style="color:#FF0000">Attention</span>&nbsp;en cas d&#39;utililisation du type <u><strong>string</strong></u>&nbsp;on doit rajouter :</h3>\r\n\r\n<p><u>(<span style="color:#000080">std::string</span> nomVariable)</u></p>\r\n\r\n<p>&nbsp;</p>\r\n', '<p><span style="background-color:#DDA0DD">#ifndef MATH_H_INCLUDED</span><br />\r\n<span style="background-color:#DDA0DD">#define MATH_H_INCLUDED</span></p>\r\n\r\n<p>#include &lt;string&gt; <span style="color:#006400">// Si la fonction contien du texte&nbsp;</span></p>\r\n\r\n<p><span style="color:#000080">void </span>afficherMessage(<span style="color:#000080">std::string</span> message);&nbsp;<span style="color:#006400">//La fonction qui utilise string doit avoire </span><span style="color:#FFFFFF"><span style="background-color:#FF0000">std ::string</span></span><span style="color:#006400">&nbsp;avans le nom de la variable</span></p>\r\n\r\n<p><span style="color:#000080">int </span>ajouteDeux(<span style="color:#000080">int </span>nombreRecu); <span style="color:#006400">//&nbsp;N&#39;oubliez pas le point-virgule ici !</span></p>\r\n\r\n<p><span style="background-color:#DDA0DD">#endif // MATH_H_INCLUDED</span></p>\r\n', 'openclassrooms.com', '0', '0', '0', 28),
(72, 'inclure', 'dans le main.cpp', '<p>Une fois que le fichier d&rsquo;en-t&ecirc;te est le fichier code source sont correctement remplie</p>\r\n\r\n<p>On peux inclure tout cela dans le fichier&nbsp;<code>main.cpp</code></p>\r\n', '<p><span style="background-color:#4B0082">#include</span> &quot;MonFichier.h&quot;</p>\r\n', 'openclassrooms.com', '0', '0', '0', 28),
(73, 'documenter', 'ses fonctions', '<h3>On utilise g&eacute;n&eacute;ralement le fichier d&rsquo;en-t&ecirc;te pour y placer le descriptif de la fonction</h3>\r\n\r\n<p>&nbsp;On y fait g&eacute;n&eacute;ralement figurer trois choses :</p>\r\n\r\n<ol>\r\n	<li>\r\n	<p>ce que fait la fonction ;</p>\r\n	</li>\r\n	<li>\r\n	<p>la liste des ses arguments ;</p>\r\n	</li>\r\n	<li>\r\n	<p>la valeur retourn&eacute;e.</p>\r\n	</li>\r\n</ol>\r\n\r\n<p>&nbsp;</p>\r\n', '<h3>Notation possible n&deg;1&nbsp;:</h3>\r\n\r\n<p><span style="color:#006400">/*<br />\r\n&nbsp;* Fonction qui ajoute 2 au nombre re&ccedil;u en argument<br />\r\n&nbsp;* - nombreRecu : Le nombre auquel la fonction ajoute 2<br />\r\n&nbsp;* Valeur retourn&eacute;e : nombreRecu + 2<br />\r\n&nbsp;*/</span><br />\r\nint ajouteDeux(int nombreRecu);</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Notation possible n&deg;2&nbsp;:</p>\r\n\r\n<p><span style="color:#006400">/**<br />\r\n&nbsp;* \\brief Fonction qui ajoute 2 au nombre re&ccedil;u en argument<br />\r\n&nbsp;* \\param nombreRecu &nbsp;Le nombre auquel la fonction ajoute 2<br />\r\n&nbsp;* \\return nombreRecu + 2<br />\r\n&nbsp;*/</span><br />\r\nint ajouteDeux(int nombreRecu);</p>\r\n', 'openclassrooms.com', '0', '0', '0', 29),
(74, 'les arguments', 'facultatif', '<h3>Il est possible de rendre des arguments facultatifs&nbsp;:</h3>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Tout ce passe dans l&rsquo;en-t&ecirc;te</p>\r\n\r\n	<ul>\r\n		<li>\r\n		<p>On les&nbsp;rends facultatifs en les remplissant par d&eacute;faut .</p>\r\n		</li>\r\n		<li>\r\n		<p>Tous ou partiellement .</p>\r\n		</li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<blockquote>\r\n<h3><strong><u>???<font color="#ff0000">ATTENTION :</font></u></strong>&nbsp;<u>les variables pr&eacute;s-remplie ne peuvent etre qu&#39;as droite !</u></h3>\r\n</blockquote>\r\n\r\n<p>l&#39;astuce consiste a <strong><u><span style="color:#FF8C00">placer les variable facultatif a la fin</span></u></strong>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul style="margin-left: 40px;">\r\n</ul>\r\n', '<p><span style="color:#0000CD">int </span>nombreDeSecondes(<span style="color:#0000CD">int </span><span style="color:#FF0000">heures</span>, <span style="color:#0000CD">int</span> <span style="color:#FF0000">minutes&nbsp;</span><span style="background-color:#800080"><span style="color:#FF0000"> </span>= 0</span>, <span style="color:#0000CD">int </span><span style="color:#FF0000">secondes </span><span style="background-color:#800080">= 0</span>); <span style="color:#006400">// OK</span></p>\r\n\r\n<p>int nombreDeSecondes( int minutes<span style="background-color:#800080"> = 0 </span>,&nbsp;int heures, int secondes<span style="background-color:#800080"> = 0</span>); <span style="color:#006400">//FAUX</span> &nbsp;</p>\r\n\r\n<p><span style="color:#0000CD">int </span>nombreDeSecondes(<span style="color:#0000CD">int</span> <span style="color:#FF0000">minutes&nbsp;</span>, <span style="color:#0000CD">int </span><span style="color:#FF0000">heures&nbsp;</span><span style="background-color:#800080"><span style="color:#FF0000"> </span>= 0</span>&nbsp;, <span style="color:#0000CD">int </span><span style="color:#FF0000">secondes </span><span style="background-color:#800080">= 0</span>); <span style="color:#006400">// OK</span></p>\r\n\r\n<p>int nombreDeSecondes( int minutes<span style="background-color:#800080"> = 0 </span>,&nbsp; int secondes<span style="background-color:#800080"> = 0</span> ,&nbsp;int heures); <span style="color:#006400">//FAUX</span> &nbsp;</p>\r\n', 'openclassrooms.com', '0', '0', '0', 27);
INSERT INTO `article` (`Idarticle`, `TITRE`, `SOUSTITRE`, `DESCRIPTION`, `EXEMPLE`, `SOURCE`, `img1`, `img2`, `img3`, `IdEtiquette`) VALUES
(75, 'tableau statique', 'cr&eacute;e est remplir ', '<h3>La d&eacute;claration d&#39;un tableau est tr&egrave;s similaire &agrave; celle d&#39;une variable (figure suivante) :</h3>\r\n\r\n<blockquote>\r\n<p><img alt="D??claration d''un tableau statique" id="r-1905903" src="https://user.oc-static.com/files/287001_288000/287407.png" /></p>\r\n</blockquote>\r\n\r\n<p>Pour&nbsp;<span style="color:#FFA500">La taille</span> des tableaux , il est conseiller d&#39;utiliser des <span style="color:#0000FF">constante</span></p>\r\n\r\n<p><u><strong><span style="color:#FF0000">attention !</span></strong></u>&nbsp;<u>La valeur 1 porte ne num&eacute;ro 0</u> !&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', '<p>#include &lt;iostream&gt;<br />\r\nusing namespace std;</p>\r\n\r\n<p>int main()<br />\r\n{<br />\r\n&nbsp; &nbsp;<span style="color:#0000FF">int </span><span style="color:#FFFFFF"><span style="background-color:#800080">const</span> </span><span style="color:#FF0000">nombreNotes</span>(<span style="color:#FFA500">2</span>); &nbsp;<span style="color:#006400">// D&eacute;claration de la variable charg&eacute; de la taille du tableau</span></p>\r\n\r\n<p>&nbsp; &nbsp;<span style="color:#0000FF">double </span><span style="color:#FF0000">notes[</span><span style="color:#FFA500">nombreNotes</span><span style="color:#FF0000">]</span>;</p>\r\n\r\n<p><span style="color:#FF0000">&nbsp; &nbsp;notes[</span><span style="color:#FFA500">0</span><span style="color:#FF0000">] </span>= <span style="color:#FFA500">12.5</span>; <span style="color:#006400">//On commence TOUJOURS a 0</span><br />\r\n<span style="color:#FF0000">&nbsp; &nbsp;notes[</span><span style="color:#FFA500">1</span><span style="color:#FF0000">]</span> = <span style="color:#FFA500">19.5</span>;<br />\r\n<span style="color:#FF0000">&nbsp; &nbsp;notes[</span><span style="color:#FFA500">2</span><span style="color:#FF0000">] </span>= <span style="color:#FFA500">6</span>.;<br />\r\n<br />\r\n&nbsp; &nbsp;<span style="color:#0000FF">double </span><span style="color:#FF0000">moyenne</span>(<span style="color:#FFA500">0</span>);<br />\r\n<span style="background-color:#00FFFF">&nbsp; &nbsp;for(int i(0); i&lt;nombreNotes; ++i)</span><br />\r\n&nbsp; &nbsp;{<br />\r\n&nbsp; &nbsp; &nbsp; <span style="color:#FF0000">moyenne </span>+=<span style="color:#FF0000"> notes[</span><span style="color:#FFA500">i</span><span style="color:#FF0000">]</span>; &nbsp; <span style="color:#006400">//On additionne toutes les notes</span><br />\r\n&nbsp; &nbsp;}<br />\r\n<span style="color:#006400">&nbsp; &nbsp;//Il ne reste donc qu&#39;&agrave; diviser par le nombre de notes</span><br />\r\n&nbsp; &nbsp;<span style="color:#FF0000">moyenne </span>/=<span style="color:#FF0000"> nombreNotes&nbsp;</span>;<br />\r\n&nbsp; &nbsp;<br />\r\n&nbsp; &nbsp;<span style="color:#0000FF">cout </span>&lt;&lt; <span style="color:#808080">&quot;Votre moyenne est : &quot;</span> &lt;&lt; <span style="color:#FF0000">moyenne</span> &lt;&lt; endl;</p>\r\n\r\n<p>&nbsp; &nbsp;return 0;<br />\r\n}</p>\r\n', 'openclassrooms.com', '0', '0', '1456177278.img3.png', 30),
(76, 'les tableau statique', 'et les fonctions', '<ol>\r\n	<li>La premi&egrave;re restriction est qu&#39;<em>on ne peut pas &eacute;crire une fonction qui renvoie un tableau statique</em>.&nbsp;\r\n	<ul>\r\n		<li>\r\n		<pre>\r\nL&#39;utilisation de return pour un tableau est inutile </pre>\r\n		</li>\r\n	</ul>\r\n	</li>\r\n	<li>La deuxi&egrave;me restriction est qu&#39;un tableau statique est&nbsp;<em>toujours pass&eacute; par r&eacute;f&eacute;rence</em>.&nbsp;\r\n	<ul>\r\n		<li>Et il n&#39;y a pas besoin d&#39;utiliser l&#39;esperluette (<strong>&amp;</strong>)&nbsp;</li>\r\n	</ul>\r\n	</li>\r\n	<li>\r\n	<pre>\r\nUne fonction contient <u><strong>TOUJOURS</strong></u> comme <span style="color:#FFA500">param&egrave;tre </span>sa <span style="color:#0000FF">taille</span></pre>\r\n	</li>\r\n	<li>\r\n	<pre>\r\nLe nom du tableau a des crochets vides</pre>\r\n	</li>\r\n</ol>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', '<p><span style="background-color:#00FFFF">double moyenne(double tableau[], int tailleTableau)</span><br />\r\n{<br />\r\n&nbsp; &nbsp;<span style="color:#0000FF">double </span><span style="color:#FF0000">moyenne</span>(<span style="color:#FFA500">0</span>);<br />\r\n<span style="background-color:#00FFFF">&nbsp; &nbsp;for(int i(0); i&lt;tailleTableau; ++i)</span><br />\r\n&nbsp; &nbsp;{<br />\r\n&nbsp; &nbsp; &nbsp; <span style="color:#FF0000">moyenne </span>+= <span style="color:#FF0000">tableau</span>[<strong><span style="color:#FFA500">i</span></strong>]; &nbsp; <span style="color:#006400">//On additionne toutes les valeurs</span><br />\r\n&nbsp; &nbsp;}<br />\r\n&nbsp; &nbsp;<span style="color:#FF0000">moyenne </span>/= <span style="color:#FF0000">tailleTableau</span>;</p>\r\n\r\n<p>&nbsp; &nbsp;return moyenne;<br />\r\n}</p>\r\n', '', '0', '0', '0', 30),
(77, 'ajouter / supprimer', 'push_back() / pop_back()', '<ol>\r\n	<li>\r\n	<p>Il faut utiliser la fonction&nbsp;<strong><span style="color:#0000FF"><code>push_back()</code></span></strong>. On &eacute;crit le nom du tableau, suivi d&#39;un point ,&nbsp;entre parenth&egrave;ses, la <u><span style="color:#FFA500">valeur qui va remplir la nouvelle case</span></u>.</p>\r\n	</li>\r\n	<li>On peut supprimer la derni&egrave;re case d&#39;un tableau en utilisant la fonction&nbsp;<strong><span style="color:#0000FF"><code>pop_back()</code></span></strong>&nbsp;de la m&ecirc;me mani&egrave;re que&nbsp;<code>push_back()</code>, sauf qu&#39;il n&#39;y a <u><span style="color:#FFA500">rien &agrave; mettre entre les parenth&egrave;ses</span></u>.</li>\r\n</ol>\r\n\r\n<p><u><em><strong>Attention tout de m&ecirc;me &agrave; ne pas trop supprimer de cases ! Un tableau ne peut pas contenir moins de 0 &eacute;l&eacute;ments.</strong></em></u></p>\r\n', '<p><span style="color:#0000FF">vector&lt;int&gt;</span> <span style="color:#FF0000">tableau</span>(<span style="color:#FFA500">3</span>,<span style="color:#FFA500">2</span>); &nbsp;<span style="color:#006400">//Un tableau de 3 entiers valant tous 2</span><br />\r\n<span style="color:#0000FF">tableau.push_back</span>(<span style="color:#FFA500">8</span>);&nbsp;<span style="color:#006400">//On ajoute une 4&egrave;me case au tableau qui contient la valeur 8</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style="color:#0000FF">vector&lt;int&gt;</span> <span style="color:#FF0000">tableau</span>(<span style="color:#FFA500">3</span>,<span style="color:#FFA500">2</span>); &nbsp;<span style="color:#006400"> //Un tableau de 3 entiers valant tous 2</span><br />\r\n<span style="color:#0000FF">tableau.pop_back() </span>;<span style="color:#006400"> //Et hop ! Plus que 2 cases</span><br />\r\n<span style="color:#0000FF">tableau.pop_back() </span>;<span style="color:#006400"> //Et hop ! Plus que 1 case</span></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'openclassrooms.com', '0', '0', '0', 33),
(78, 'compter les &eacute;l&eacute;ment', 'd''un tableau dynamique', '<ul>\r\n	<li>Comme la taille peut changer, on ne sait pas de mani&egrave;re certaine combien d&#39;&eacute;l&eacute;ments contient un tableau.</li>\r\n</ul>\r\n\r\n<h3><span style="color:#FFFFFF"><code><span style="background-color:#0000FF">size()</span></code></span>. Avec&nbsp;<code><span style="color:#FFFFFF"><span style="background-color:#FF0000">tableau</span><span style="background-color:#0000FF">.size()</span></span></code>, on r&eacute;cup&egrave;re un entier correspondant au <span style="color:#FFA500"><u>nombre d&#39;&eacute;l&eacute;ments de&nbsp;<code>tableau</code></u></span></h3>\r\n\r\n<p>&nbsp;</p>\r\n', '<p><span style="color:#0000FF">vector</span>&lt;<span style="color:#800080">int</span>&gt; <span style="color:#FF0000">tableau</span>(<span style="color:#FFA500">5</span>,<span style="color:#FFA500">4</span>); <span style="color:#006400">&nbsp;//Un tableau de 5 entiers valant tous 4</span><br />\r\n<span style="color:#0000FF">int </span><span style="color:#800080">const </span><span style="color:#FF0000">taille</span>(<span style="color:#FF0000">tableau</span><span style="color:#0000FF">.size()</span>);<br />\r\n<span style="color:#006400">//Une variable qui contient la taille du tableau<br />\r\n//La taille peut varier mais la valeur de cette variable ne changera pas<br />\r\n//On utilise donc une constante<br />\r\n//&Agrave; partir d&#39;ici, la constante &#39;taille&#39; vaut donc 5</span></p>\r\n', 'openclassrooms.com', '0', '0', '0', 33),
(79, 'tableaux dynamiques', 'ou vector ', '<h3>au tout d&eacute;but de votre programme. Il faut ajouter la ligne&nbsp;<span style="color:#EE82EE"><code><span style="background-color:#0000FF">#include &lt;vector&gt;</span></code></span>&nbsp;pour utiliser ces tableaux.</h3>\r\n\r\n<p><strong><u>On utilise la syntaxe pr&eacute;sent&eacute;e en figure suivante :</u></strong></p>\r\n\r\n<ul style="margin-left:40px">\r\n	<li><img alt="D??claration d''un vector" id="r-1905973" src="https://user.oc-static.com/files/287001_288000/287408.png" /></li>\r\n</ul>\r\n\r\n<ol>\r\n	<li>on &eacute;crit la taille entre parenth&egrave;ses et non entre crochets.</li>\r\n	<li>\r\n	<p>On peut directement remplir toutes les cases du tableau en ajoutant un deuxi&egrave;me argument entre les parenth&egrave;ses .</p>\r\n	</li>\r\n	<li>\r\n	<pre>\r\n&nbsp;</pre>\r\n\r\n	<p>On peut d&eacute;clarer un tableau sans cases en ne mettant pas de parenth&egrave;ses du tout .</p>\r\n\r\n	<p>&nbsp;</p>\r\n	</li>\r\n</ol>\r\n', '<p>#include &lt;iostream&gt;<br />\r\n<span style="color:#800080">#include &lt;vector&gt;</span><span style="color:#006400"> //Ne pas oublier !</span><br />\r\nusing namespace std;</p>\r\n\r\n<p>int main()<br />\r\n{<br />\r\n&nbsp; &nbsp;<span style="color:#0000FF">vector</span>&lt;<span style="color:#800080">double</span>&gt; <span style="color:#FF0000">notes</span>;</p>\r\n\r\n<p>&nbsp; &nbsp;<span style="color:#0000FF">vector</span>&lt;<span style="color:#800080">double</span>&gt; <span style="color:#FF0000">preRemplie</span>(<span style="color:#FFA500">8 </span>,<span style="color:#FFA500">16</span>); <span style="color:#006400">//8 cases&nbsp;contenant toutes&nbsp;16&nbsp;</span></p>\r\n\r\n<p><span style="color:#0000FF">int</span>&nbsp;<span style="color:#FF0000">nombre</span>(<span style="color:#FFA500">8</span>);<br />\r\n&nbsp; &nbsp;<span style="color:#0000FF">double </span><span style="color:#FF0000">moyenne</span>(<span style="color:#FFA500">0</span>);<br />\r\n<span style="background-color:#008080">&nbsp; &nbsp;for(int i(0); i&lt;nombre; ++i)&nbsp;</span><br />\r\n&nbsp; &nbsp;{<br />\r\n&nbsp; &nbsp; &nbsp; <span style="color:#FF0000">moyenne </span>+= <span style="color:#FF0000">notes</span>[<span style="color:#FFA500"><strong>i</strong></span>]; &nbsp;<span style="color:#006400"> //On additionne toutes les notes</span><br />\r\n&nbsp; &nbsp;}</p>\r\n\r\n<p>&nbsp; &nbsp;<span style="color:#FF0000">moyenne </span>/=<span style="color:#FF0000"> nombre </span>;<br />\r\n&nbsp; &nbsp;<br />\r\n&nbsp; &nbsp;<span style="color:#0000FF">cout </span>&lt;&lt;<span style="color:#696969"> &quot;Votre moyenne est : &quot;</span> &lt;&lt; <span style="color:#FF0000">moyenne </span>&lt;&lt; endl;</p>\r\n\r\n<p>&nbsp; &nbsp;return 0;<br />\r\n}</p>\r\n\r\n<ol>\r\n</ol>\r\n', 'openclassrooms.com', '0', '0', '0', 30),
(80, 'les fonctions complexes', 'string ; vector ; ex...', '<h3>Attention , pour toute les fonctions qui utilise <span style="color:#008000"><span style="background-color:#000000">#include &lt;exemple&gt;</span></span>,&nbsp;</h3>\r\n\r\n<ol>\r\n	<li>\r\n	<pre>\r\nOn doit l&#39;utiliser dans le <span style="color:#FFFFFF"><span style="background-color:#0000CD">.cpp</span></span></pre>\r\n	</li>\r\n</ol>\r\n\r\n<ul>\r\n	<li>\r\n	<ul>\r\n		<li>\r\n		<pre>\r\nTout en haut</pre>\r\n		</li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<ol start="2">\r\n	<li>\r\n	<pre>\r\n<span style="color:#0000CD">using namespace std</span>;</pre>\r\n	</li>\r\n</ol>\r\n\r\n<ul>\r\n	<li>\r\n	<ul>\r\n		<li>\r\n		<pre>\r\njuste apr&eacute;s les <span style="color:#008000"><span style="background-color:#000000">#include &lt;exemple&gt;</span></span></pre>\r\n		</li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<ol start="3">\r\n	<li>\r\n	<pre>\r\nParfois dans le <span style="color:#FFFFFF"><span style="background-color:#0000CD">.h</span></span></pre>\r\n	</li>\r\n</ol>\r\n', '<p><span style="color:#FFFFFF"><span style="background-color:#006400">#include &lt;iostream&gt;</span><br />\r\n<span style="background-color:#006400">#include &lt;string&gt;</span><br />\r\n<span style="background-color:#006400">#include &lt;vector&gt;</span><br />\r\n<span style="background-color:#006400">#include &lt;cmath&gt;</span></span><br />\r\n<span style="color:#FFFFFF"><span style="background-color:#0000CD">using namespace std;</span></span></p>\r\n\r\n<p><br />\r\n<span style="background-color:#40E0D0">void afficherMessage(</span><span style="color:#FFFFFF"><span style="background-color:#0000CD">std::</span></span><span style="background-color:#40E0D0">string message)</span><br />\r\n{</p>\r\n\r\n<p>&nbsp; &nbsp; cout &lt;&lt;endl &lt;&lt; &nbsp;message &lt;&lt; endl ;</p>\r\n\r\n<p>}</p>\r\n', 'openclassrooms.com', '0', '0', '0', 28),
(81, 'afficher un tableaux', 'statique', '<ol>\r\n	<li>Le gros point fort des tableaux, c&#39;est qu&#39;on peut les parcourir en utilisant une boucle.\r\n	<ul>\r\n		<li>On peut ainsi effectuer une action sur chacune des cases d&#39;un tableau, l&#39;une apr&egrave;s l&#39;autre : par exemple afficher le contenu des cases.</li>\r\n	</ul>\r\n	</li>\r\n	<li>attention&nbsp;&agrave; ne pas d&eacute;passer la taille du tableau, sous peine de voir votre programme planter.&nbsp;\r\n	<ul>\r\n		<li>\r\n		<p>la premi&egrave;re case du tableau a le num&eacute;ro 0, la deuxi&egrave;me le num&eacute;ro 1, et ainsi de suite.</p>\r\n		</li>\r\n	</ul>\r\n	</li>\r\n</ol>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', '<p><span style="color:#0000CD">int </span><span style="color:#800080">const </span><span style="color:#FF0000">nombreMeilleursScores</span>(<span style="color:#FF8C00">5</span>); &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span style="color:#006400"> //La taille du tableau</span></p>\r\n\r\n<p><span style="color:#0000CD">int </span><span style="color:#FF0000">meilleursScores</span>[<span style="color:#FF8C00">nombreMeilleursScores</span>]; &nbsp; <span style="color:#006400">//D&eacute;claration du tableau</span></p>\r\n\r\n<p><span style="color:#FF0000">meilleursScores</span>[<span style="color:#FFA500">0</span>] = <span style="color:#FFA500">118218</span>; &nbsp;<span style="color:#006400">//Remplissage de la case</span></p>\r\n\r\n<p><span style="color:#FF0000">meilleursScores</span>[<span style="color:#FFA500">1</span>] = <span style="color:#FFA500">59155</span>; &nbsp;<span style="color:#006400">//Remplissage de la case</span></p>\r\n\r\n<p><span style="color:#FF0000">meilleursScores</span>[<span style="color:#FFA500">2</span>] = <span style="color:#FFA500">9726582</span>; &nbsp;<span style="color:#006400">//Remplissage de la case</span></p>\r\n\r\n<p><span style="color:#FF0000">meilleursScores</span>[<span style="color:#FFA500">3</span>] = <span style="color:#FFA500">354792</span>; &nbsp;<span style="color:#006400">//Remplissage de la case</span></p>\r\n\r\n<p><span style="color:#FF0000">meilleursScores</span>[<span style="color:#FFA500">4</span>] = <span style="color:#FFA500">78513057</span>; &nbsp;<span style="color:#006400">//Remplissage de la case</span></p>\r\n\r\n<p><span style="background-color:#008080">for(int i(0); i&lt;nombreMeilleursScores; ++i)</span><br />\r\n{&nbsp;<br />\r\n&nbsp; &nbsp; <span style="color:#0000CD">cout</span> &lt;&lt; <span style="color:#FF0000">meilleursScores</span>[<span style="color:#FFA500"><strong>i</strong></span>] &lt;&lt; endl;<br />\r\n}</p>\r\n', 'openclassrooms.com', '0', '0', '0', 30),
(82, 'code de d&eacute;marrage ', 'main()', '<h3>Lorsqu&#39;on d&eacute;marre un nouveaux projet voil&agrave; la page de main&nbsp;</h3>\r\n', '<p>#include &lt;iostream&gt;</p>\r\n\r\n<p>using namespace std;</p>\r\n\r\n<p>int main()<br />\r\n{<br />\r\n&nbsp; &nbsp; cout &lt;&lt; &quot;Hello world!&quot; &lt;&lt; endl;<br />\r\n&nbsp; &nbsp; return 0;<br />\r\n}</p>\r\n', 'openclassrooms.com', '0', '0', '0', 12),
(83, 'les tableaux dynamiques', 'et les fonctions', '<ol>\r\n	<li>Comme pour n&#39;importe quel autre type, il suffit de mettre&nbsp;<span style="color:#FFFFFF"><code><span style="background-color:#0000CD">vector&lt;type&gt;</span></code></span>&nbsp;<u>en argument</u>.\r\n	<ul>\r\n		<li>Gr&acirc;ce &agrave; la fonction&nbsp;<span style="color:#FFFFFF"><code><span style="background-color:#0000CD">size()</span></code></span>&nbsp;&nbsp;, il n&#39;y a m&ecirc;me pas besoin d&#39;ajouter un deuxi&egrave;me argument pour la taille du tableau&nbsp;</li>\r\n	</ul>\r\n	</li>\r\n	<li>si le tableau contient beaucoup d&#39;&eacute;l&eacute;ments, le copier prendra du temps.\r\n	<ul>\r\n		<li>Il vaut donc mieux utiliser <span style="color:#FFA500"><strong><u>le&nbsp;passage par r&eacute;f&eacute;rence</u></strong></span> <span style="color:#0000CD"><u><em><strong>constante</strong></em></u></span> pour optimiser la copie???</li>\r\n		<li>Dans ce cas, le tableau dynamique ne <strong><u>peut pas &ecirc;tre modifi&eacute;</u></strong>.</li>\r\n		<li>Pour changer le contenu du tableau, il faut utiliser <span style="color:#FFA500"><em><strong><u>un passage par r&eacute;f&eacute;rence</u></strong></em></span> tout simple (sans le&nbsp;<span style="color:#FFFFFF"><code><span style="background-color:#0000CD">const</span></code></span>&nbsp;donc).</li>\r\n	</ul>\r\n	</li>\r\n	<li>Pour appeler une fonction <span style="color:#FF0000"><strong><u>recevant un vector comme argument</u></strong></span>\r\n	<ul>\r\n		<li>il suffit de mettre <span style="color:#FFA500"><em><u>le nom du tableau dynamique</u></em></span> comme param&egrave;tre <u><span style="color:#0000FF">entre</span></u> les <u><span style="color:#0000FF">parenth&egrave;ses</span></u> lors de l&#39;appel</li>\r\n	</ul>\r\n	</li>\r\n</ol>\r\n\r\n<h3><span style="color:#FF0000"><strong><u>Attention</u></strong></span> On ne peut passer un&nbsp;<span style="color:#FFFFFF"><strong><u><span style="background-color:#0000CD">vector&lt;int&gt;</span></u></strong></span>&nbsp;que &agrave; une fonction recevant un&nbsp;<span style="color:#FFFFFF"><strong><u><span style="background-color:#0000CD">vector&lt;int&gt;</span></u></strong></span>&nbsp;et pas &agrave; une fonction attendant un&nbsp;<span style="color:#FFFFFF"><strong><u><span style="background-color:#0000CD">vector&lt;char&gt;</span></u></strong></span>. <span style="color:#FF0000"><strong><em><u><span style="background-color:#000000">On ne peut pas m&eacute;langer les types des &eacute;l&eacute;ments stock&eacute;s !</span></u></em></strong></span></h3>\r\n', '<p><span style="color:#006400">//Une fonction recevant un tableaux dynamiques&nbsp;argument</span><br />\r\n<span style="background-color:#008080">void fonction(vector&lt;int&gt; a)</span><br />\r\n{<br />\r\n&nbsp; &nbsp; //&hellip;<br />\r\n}</p>\r\n\r\n<p><span style="color:#006400">//Une fonction recevant un tableaux dynamiques <u><em><strong>constant </strong></em></u>argument</span><br />\r\n<span style="background-color:#008080">void fonction(vector&lt;int&gt; </span><span style="background-color:#0000CD">const</span><span style="background-color:#008080">&nbsp;a)</span><br />\r\n{<br />\r\n&nbsp; &nbsp; //&hellip;<br />\r\n}</p>\r\n\r\n<pre>\r\n<span style="color:#006400">//Une fonction recevant un tableaux dynamiques <strong><u>constant</u></strong>&nbsp; ET &nbsp;en <strong><em><u>passant par r&eacute;f&eacute;rence</u></em></strong> comme argument </span></pre>\r\n\r\n<p><br />\r\n<span style="background-color:#008080">void fonction(vector&lt;int&gt; </span><span style="background-color:#0000CD">const&amp;</span><span style="background-color:#008080">&nbsp;a)</span><br />\r\n{<br />\r\n&nbsp; &nbsp; //&hellip;<br />\r\n}</p>\r\n', 'openclassrooms.com', '0', '0', '0', 30),
